mapper.insert:
INSERT INTO c6_ask_file
(
askFileId
,askId
,ovFilename
,svFilename
,fileSize
,createdDate
,updatedDate
)
VALUES
(
#{askId} || '-F' || TO_CHAR(c6_ask_file_seq.NEXTVAL, '0000')
,#{askId}
,#{ovFilename}
,#{svFilename}
,#{fileSize}
,SYSDATE
,SYSDATE
)

mapper.update:
UPDATE c6_ask_file
SET
ovFilename = #{ovFilename}
,svFilename = #{svFilename}
,fileSize = #{fileSize}
,updatedDate = SYSDATE
WHERE askFileId = #{askFileId}
