mapper.insert:
INSERT INTO c6_ask
(
askId
,memberId
,exhibitionId
,title
,content
,createdDate
,updatedDate
)
VALUES
(
#{exhibitionId} || '-A' || TO_CHAR(c6_ask_seq.NEXTVAL, '0000')
,#{memberId}
,#{exhibitionId}
,#{title}
,#{content}
,SYSDATE
,SYSDATE
)

mapper.delete:
DELETE
FROM c6_ask
WHERE askId = #{askId}
  AND memberId = #{memberId}

mapper.update:
UPDATE c6_ask
SET
title = #{title}
,content = #{content}
,updatedDate = SYSDATE
WHERE askId = #{askId}
  AND memberId = #{memberId}
