mapper.delete:
DELETE
FROM c6_member
WHERE memberId = #{memberId}
  AND memberPwd = #{memberPwd}

mapper.update:
UPDATE c6_member
SET
memberPwd = #{memberPwd}
,memberName = #{memberName}
,memberBirth = #{memberBirth}
,memberEmail = #{memberEmail}
,memberPhone = #{memberPhone}
,regDate = SYSDATE
WHERE memberId = #{memberId}
