mapper.insert:
insert into c6_board
(
board_id
,member_id
,category
,title
,content
,create_date
,update_date
)
values
(
c6_board_seq.nextval
,#{member_id}
,#{category}
,#{title}
,#{content}
,sysdate
,sysdate
)

mapper.update:
update c6_board
category =
#{category}
,title = #{title}
,content = #{content}
,update_date =
sysdate
where board_id = #{board_id}
