mapper.select:
select boardid from
c6_board where memberid = #{memberId}
and rownum=1

mapper.insert:
INSERT INTO c6_board
(
boardid
,memberid
,category
,title
,content
,createddate
,updateddate
,exhibitionid
)
VALUES
(
REPLACE('B' || TO_CHAR(c6_board_seq.NEXTVAL, '000000000'), ' ', '') 
,#{memberId}
,#{category}
,#{title}
,#{content}
,SYSDATE
,SYSDATE
,#{exhibitionId}
)

mapper.update:
UPDATE c6_board
SET
category = #{category}
,title = #{title}
,content = #{content}
,updateddate = SYSDATE
WHERE boardid = #{boardId}
