mapper.delete:
UPDATE c6_comment
SET
updatedDate = SYSDATE
,rating = 0
WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}

UPDATE c6_comment
SET
updatedDate = SYSDATE
,content = ''
WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}

mapper.update:
UPDATE c6_comment
SET
updatedDate = SYSDATE

,content = #{content}


,rating = #{rating}

WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}
