mapper.select:
SELECT memberId, exhibitionId, wished, createdDate, updatedDate
FROM c6_wishing
WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}

mapper.delete:
UPDATE c6_wishing
SET
wished = 'N'
,updatedDate = SYSDATE
WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}

mapper.update:
UPDATE c6_wishing
SET
wished = 'Y'
,updatedDate = SYSDATE
WHERE memberId = #{memberId}
  AND exhibitionId = #{exhibitionId}
